import React, { useState, FormEvent } from 'react';
import { FiChevronRight } from 'react-icons/fi';
import api from '../../services/api';

import Logo from '../../assets/logo.svg';
import { Title, Form, Repositories } from './styles';

interface Repository {
  full_name: string;
  description: string;
  owner: {
    login: string;
    avatar_url: string;
  };
}

const Dashboard: React.FC = () => {
  const [newRepo, setNewRepo] = useState('');
  const [inputError, setInputError] = useState('');
  const [repositories, setRepositories] = useState<Repository[]>([]);

  // usando o state para quando os valores forem dinâmicos, ou seja, quando os valores forem consumir dados da minha API

  async function handleAddRepository(e: FormEvent<HTMLFormElement>): Promise<void> {
    event.preventDefault();

    if (!newRepo) {
      setInputError('Digite o autor/nome do repositório');
      return;
    }


    try {
      const response = await api.get<Repository>(`repos/${newRepo}`);

      const repository = response.data;

      setRepositories([...repositories, repository]);
      setNewRepo('');
      setInputError('');
    } catch (err) {
      setInputError('Erro na busca por esse repositório');
    }
  }

  return (
    <>
      <img src={Logo} alt="Github explorer" />
      <Title> Explore repositórios do github</Title>

      <Form hasError={!!inputError} onSubmit={handleAddRepository}>

        <input
          value={newRepo}
          onChange={ e => setNewRepo(e.target.value)}

          placeholder="Digite aqui o nome do repositório" />
        <button type="submit">Pesquisar</button>
      </Form>

      {/* {inputError && <Error>{inputError}</Error>} */}

      <Repositories >
        {repositories.map(repository =>

          <a key={repository.full_name} href="test">
            <img src={repository.owner.avatar_url} alt={repository.owner.login} />
            <div>
              <strong>{repository.full_name}</strong>
              <p>{repository.description}</p>
            </div>

            <FiChevronRight size={20} />
          </a>

        )}


      </Repositories>

    </>
  )
};


export default Dashboard;
