import React, { useEffect } from 'react';
import { useRouteMatch, Link } from 'react-router-dom'; // biblioteca do hooks
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import api from '../../services/api'

import Logo from '../../assets/logo.svg';

import { Header, RepositoryInfo, Issues } from './styles';

interface RepositoryParams {
  repository: string;
}

const Repository: React.FC = () => {
  const { params } = useRouteMatch<RepositoryParams>();

  useEffect(() => {
    // api.get(`repos/${params.repository}`).then(response => {
    //   console.log(response.data);
    // });

    //   api.get(`repos/${params.repository}/issues`).then(response => {
    //     console.log(response.data);
    //   });

    async function loadData() {
      const repository = await api.get(`repos/${params.repository}`);
      const issues = await api.get(`repos/${params.repository}/issues`);

      console.log(repository);
      console.log(issues);
    }
    loadData();
    }, [params.repository]);

    return (
      <>
        <Header>
          <img src={Logo} alt="Github Explorer" />
          <Link to="/">
            <FiChevronLeft size={16} />
            Voltar
          </Link>
        </Header>

        <RepositoryInfo>
          <header>
            <img src="https://avatars.githubusercontent.com/u/67030481?v=4" alt="Wiliamis Oliveira" />
            <div>
              <strong>wiliami/wilflix</strong>
              <p>descrição do repositório</p>

            </div>
          </header>
          <ul>
            <li>
              <strong>1080</strong>
              <span>Starts</span>
            </li>
            <li>
              <strong>48</strong>
              <span>Forks</span>
            </li>
            <li>
              <strong>67</strong>
              <span>Issues abertas</span>
            </li>
          </ul>

        </RepositoryInfo>

        <Issues>
          <Link
            to="ahusaushaus">

            <div>
              <strong>Título do repositório</strong>
              <p>alguma coisa no parágrafo de descrição</p>
            </div>

            <FiChevronRight size={20} />
          </Link>
        </Issues>



      </>
    );
  }


export default Repository;
